import React, { useEffect, useState } from 'react'
import MainTitle from '../components/MainTitle'
import axios from 'axios'
// import CardStore from '../components/CardStore'

const App = () => {

const [storeItems, setStoreItems] =useState([])

useEffect(() => {
getItems()
console.log(storeItems)
},[])

const getItems = async () => {
  try {
    const res= await axios.get('https://fakestoreapi.com/products')
    setStoreItems(res.data)
  } catch (error) {
    console.log(error)

  }

}



  return (
   <div className='flex justify-center mt-20 flex-col items-center'>
    <MainTitle>
      Store cards     
    </MainTitle>

      {/* <CardStore storeItems={storeItems} tax={0.21}/> */}
      <div className='border-2 rounded-lg p-10 row'>

        {storeItems.map((item)=>( <div className='bg-slate-500 text-white border-2 rounded-lg p-10 col-4' key={item.id}>

          <img src={item.image} alt="" />
          <h2 className='text-center text-xl font-bold underline'>{item.title}</h2>
          <p className='text-gray-200'> Description: {item.description}</p>
          <p className='text-blue-700'> Category: {item.category}</p>



        </div>  ))} 
      </div>

   </div>
  )
}

export default App
